import React, { useState, useEffect } from "react";
import RaceCard from "./components/RaceCard";
import "./App.css";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  Navigate,
} from "react-router-dom";
import SignUp from "./components/SignUp";
import Login from "./components/Login";
import UserDashboard from "./components/UserDashboard";

function App() {
  const [races, setRaces] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem("currentUser")
  );

  // Keeping the existing fetchRaces and handleLogout logic

  const NavBar = () => (
    <nav className="navbar">
      <Link to="/" className="navbar__brand">
        D&D Races
      </Link>
      <div className="navbar__links">
        {isAuthenticated ? (
          <>
            <Link to="/dashboard" className="navbar__button">
              Dashboard
            </Link>
            <button onClick={handleLogout} className="navbar__button">
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="navbar__button">
              Login
            </Link>
            <Link to="/signup" className="navbar__button">
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );

  const MainComponent = () => (
    <div className="dnd">
      <h1 className="dnd__title">Races of the Realm</h1>
      <div className="dnd__races">
        {races.map((race) => (
          <RaceCard
            key={race.index}
            race={race}
            isAuthenticated={isAuthenticated}
          />
        ))}
      </div>
    </div>
  );

  // Keeping ProtectedRoute and return statement as is
}

export default App;
