import React, { useState, useEffect } from "react";
import RaceCard from "./components/RaceCard";
import "./App.css";

function App() {
  const [races, setRaces] = useState([]);

  useEffect(() => {
    const fetchRaces = async () => {
      const raceList = [
        "dwarf",
        "elf",
        "halfling",
        "human",
        "dragonborn",
        "gnome",
        "half-elf",
        "half-orc",
        "tiefling",
      ];

      try {
        const raceData = await Promise.all(
          raceList.map(async (race) => {
            const response = await fetch(
              `https://www.dnd5eapi.co/api/races/${race}`
            );

            const data = await response.json();
            return data;
          })
        );
        setRaces(raceData);
      } catch (error) {
        console.log("Error fetching races:", error);
      }
    };

    fetchRaces();
  }, []);

  return (
    <div className="dnd-container">
      <h1 className="title">Races of the Realm</h1>
      <div className="race-cards">
        {races.map((race) => (
          <RaceCard key={race.index} race={race} />
        ))}
      </div>
    </div>
  );
}
export default App;
