import React, { useState, useEffect, useCallback } from "react";
import RaceCard from "./RaceCard";
import "./UserDashboard.css";

const UserDashboard = () => {
  const [savedRaces, setSavedRaces] = useState([]);
  const [availableRaces, setAvailableRaces] = useState([]);
  const currentUser = JSON.parse(localStorage.getItem("currentUser"));

  // Keeping the existing logic for loadSavedRaces, useEffect, and handleSaveRace

  return (
    <div className="dashboard">
      <h1 className="dashboard__title">Welcome, {currentUser.username}!</h1>

      <section className="dashboard__section dashboard__section--saved">
        <h2 className="dashboard__subtitle">Your Saved Races</h2>
        <div className="dashboard__grid">
          {savedRaces.map((race) => (
            <RaceCard key={race.index} race={race} saved={true} />
          ))}
        </div>
      </section>

      <section className="dashboard__section">
        <h2 className="dashboard__subtitle">Available Races</h2>
        <div className="dashboard__grid">
          {availableRaces.map((race) => (
            <div key={race.index} className="dashboard__card">
              <RaceCard race={race} />
              <button
                className="dashboard__button"
                onClick={() => handleSaveRace(race)}
                disabled={savedRaces.some(
                  (saved) => saved.index === race.index
                )}
              >
                {savedRaces.some((saved) => saved.index === race.index)
                  ? "Saved"
                  : "Save Race"}
              </button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default UserDashboard;
